import React from "react";
import Input from "components/Input";
import Link from "next/link";
import Head from "next/head";
import { useMutationHooks } from "hooks/useMutationHook";
import * as AuthSevice from "services/authService"
import * as Yup from "yup";
import { useFormik } from "formik";
import { useEffect } from 'react';
import { useRouter } from 'next/navigation';
import LoadingCustom from "components/Loading";
import { toast } from "react-toastify";
import jwt_decode from "jwt-decode";
import { updateAuth } from "redux/slice/authSlice";
import { useDispatch } from "react-redux";

export default function Login() {
  const { push } = useRouter();
  const dispatch = useDispatch()
  const mutation = useMutationHooks((data) => AuthSevice.loginUser(data));
  const { data, isLoading } = mutation;
  const formik = useFormik({
    initialValues: {
      email: "",
      password: "",
    },
    validationSchema: Yup.object({
      email: Yup.string()
        .required("Required")
        .matches(
          /^[\w-\\.]+@([\w-]+\.)+[\w-]{2,4}$/,
          "Chưa đúng định dạng email!"
        ),
      password: Yup.string()
        .required("Required")
        .matches(
          /^(?=.*[A-Za-z])(?=.*\d)[A-Za-z\d]{8,}$/,
          "Tối thiểu 8 ký tự, ít nhất một chữ cái và một số!"
        ),
    }),
    onSubmit: (values) => {
      mutation.mutate(values);
    },
  });

  const notify = () => toast(data?.message, {
    position: "top-right",
    autoClose: 3000,
    hideProgressBar: true,
    closeOnClick: true,
    pauseOnHover: false,
    draggable: false,
    progress: undefined,
    theme: "dark",
    });

    const handleLoginSuccess = () =>{
      push("/")
    }

  useEffect(() => {
    if (data?.status === "ERR") {
      notify();
    }
    if(data?.status === "OK"){
      handleLoginSuccess()
      localStorage.setItem("access_token",JSON.stringify(data?.access_token))
      if(data?.access_token){
        const decoded = jwt_decode(data?.access_token)
        if(decoded?.id){
          handleGetDetailUser(decoded?.id,data?.access_token)
        }
      }
    }
  }, [data]);

  const handleGetDetailUser = async (id,token) => {
    const res = await AuthSevice.getDetailUser(id, token)
    dispatch(updateAuth({...res?.data, access_token: token}))
}
  return (
    <section className="bg-[#161D31] w-screen h-screen flex items-center justify-center overflow-hidden">
      <Head>
        <title>Đăng nhập</title>
      </Head>
      {isLoading && <LoadingCustom tip="Đang xác nhận thông tin"/>}
      <div className="p-6 relative min-w-[400px] z-10 rounded-lg bg-[#283046]
      after:z-[-1] after:bg-no-repeat after:left-[-20%] after:top-[-20%] after:contents-[''] after:w-[250px] after:h-[240px] after:absolute after:bg-[url('data:image/png;base64,')]
      before:z-[-1] before:bg-no-repeat before:right-[-30%] before:bottom-[-10%] before:contents-[''] before:w-[290px] before:h-[280px] before:absolute before:bg-[url('data:image/png;base64,')]
      ">
      
        <div className="w-full py-6 flex justify-center b">
          <img 
            src="https://creator.vtruyen.com/img/logo.36f34a9f.svg"
            alt="?"
            width={40}
          />
        </div>
        <h1 className="text-[15px] mb-3">Chào mừng đến với vCreator 👋</h1>
        <p className="text-[13px] mb-2">
          Đăng nhập và bắt đầu viết gì đó thôi nào
        </p>
        <form className="flex flex-col gap-4" onSubmit={formik.handleSubmit}>
          <Input err={formik.errors.email} value={formik.values.email} onChange={formik.handleChange} id="email" name="email" type="text" title="Email" />
          <Input err={formik.errors.password} value={formik.values.password} onChange={formik.handleChange} id="password" name="password" type="password" title="Mật khẩu" />
          <div className="flex items-center text-[12px] gap-2">
            <input type="checkbox" className="cursor-pointer" />
            Ghi nhớ
          </div>
          <button type="submit" className="bg-[#7367F0] py-2 text-[14px] rounded-lg">
            Đăng nhập
          </button>
          <div className="text-[12px] flex items-center justify-center">
            <span>
              Chưa có tài khoản?{" "}
              <Link className="text-[#7367F0]" href="/register">
                Tạo tài khoản
              </Link>
            </span>
          </div>
        </form>
      </div>
    </section>
  );
}
